                block_layout=theme_settings.prodPageBlock_2_layout
                block_id="halo-related-products"
                list="Related Products"
            }}
        {{else if theme_settings.prodPageBlock_2 '==' 'Customers'}}
            {{> components/products/related
                products=product.similar_by_views
                productsnew=products.new
                title=theme_settings.prodPageBlock_2_title
                block_column=theme_settings.prodPageBlock_col
                block_layout=theme_settings.prodPageBlock_2_layout
                block_id="halo-similar-products"
                list="Customers Also Viewed"
            }}
        {{/if}}

        {{> components/halothemes/product/halo-recent-viewed-products}}
    </div>

    {{> components/products/schema}}
	
	{{> components/doogma-pack/doogma-pack-product/doogma-pdp-script}}
    {{> components/doogma-pack/session-pack/session-management}}
    
    <script>
   (function () {
  const CANVAS_ID = 'dynaviewer-canvas';
  const WRAPPER_ID = 'dynaviewer-wrapper';

  // 1. Inject CSS
  const style = document.createElement('style');
  style.innerHTML = `
  
    .productView-image.disable-zoom .easyzoom-flyout{
        display:none;
    }

    .zoom-toggle {
      position: absolute;
      bottom: 8px;
      right: 8px;
    //   background: rgba(0,0,0,.6);
      border-radius: 50%;
      padding: 6px;
      cursor: pointer;
      z-index: 10;
      display: flex;
      align-items: center;
      justify-content: center;
      pointer-events:auto;
    }

    .zoom-toggle .icon {
      display: block;
      width: 34px;
      height: 34px;
      color: #fff;
      font-size: 18px;
      line-height: 1;
      user-select: none;
    }

    .fullscreen-wrapper {
      position: fixed !important;
      left: 0;
      top: 0;
      width: 100vw !important;
      height: 100vh !important;
      z-index: 999999;
      background: #0000006e;
      display: flex;
      align-items: start;
      justify-content: center;
    }

    .fullscreen-wrapper .zoom-toggle {
      bottom: auto;
      top: 12px;
      right: 12px;
      background: rgba(0,0,0,.7);
    }

    .fullscreen-backdrop {
      position: fixed;
      inset: 0;
      background: rgba(0,0,0,.5);
      z-index: 999998;
    }

    body.visual-fullscreen {
      overflow: hidden;
    }
    .doogma-canvas-zoom header {
      z-index: 0;
    }
    .doogma-canvas-zoom #dynaviewer-canvas {
  margin-bottom: 20px;
}
.doogma-canvas-zoom {
  pointer-events: none;
}
.doogma-canvas-zoom .zoom-toggle {
  background: transparent;
  pointer-events: auto;
}
.doogma-canvas-zoom .zoom-toggle .icon {
  width: 48px;
  height: 48px;
}
.doogma-canvas-zoom .zoom-canvas-wrapper {
  height: 98%;
  max-width: 947px;
  position: relative;
  width: 100%;
  background: #fff;
  display: flex;
  justify-content: center;
}
.doogma-canvas-zoom .zoom-toggle .icon svg {
  height: 48px !important;
  width: 48px !important;
}
body:not(.doogma-canvas-zoom) .zoom-canvas-wrapper {
  width: 100%;
  height: 100%;
}

@media (max-width: 1000px){
   .doogma-canvas-zoom .zoom-canvas-wrapper {
  margin: auto;
  width: 90%;
  height: 60%;
}
.doogma-canvas-zoom .zoom-toggle .icon svg {
  height: 40px !important;
  width: 40px !important;
}
}
@media (max-width: 440px){
   .doogma-canvas-zoom {
  width: 70%;
  overflow: hidden;
}
.doogma-canvas-zoom .zoom-toggle .icon svg {
  height: 35px !important;
  width: 35px !important;
}
}
  `;
  document.head.appendChild(style);

  // 2. Wait for DOM and Canvas
  document.addEventListener('DOMContentLoaded', () => {
    const wrapper = document.getElementById(WRAPPER_ID);
    const canvas = document.getElementById(CANVAS_ID);
    if (!wrapper || !canvas) return;

    // 3. Inject SVG Icons
    // const expandIcon = `<svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="white" stroke-width="2" stroke-linecap="round" stroke-linejoin="round">
    //   <polyline points="4 14 4 20 10 20"/>
    //   <polyline points="20 10 20 4 14 4"/>
    //   <line x1="4" y1="4" x2="10" y2="10"/>
    //   <line x1="14" y1="14" x2="20" y2="20"/>
    // </svg>`;
    
    const expandIcon = `<svg width="32px" height="32px" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg" fill="none">
  <path stroke="#ccc" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M4 8.5V4M4 4H8.5M4 4L9.5 9.5M20 8.5V4M20 4H15.5M20 4L14.5 9.5M4 15.5V20M4 20H8.5M4 20L9.5 14.5M20 15.5V20M20 20H15.5M20 20L14.5 14.5"></path>
</svg>`;

    const shrinkIcon = `<svg xmlns="http://www.w3.org/2000/svg" width="32px" height="32px" viewBox="0 0 24 24" fill="none" stroke="#ccc" stroke-width="2" stroke-linecap="round" stroke-linejoin="round">
  <path d="M15 15l6 6m-6-6v4.8m0-4.8h4.8"></path>
  <path d="M9 19.8V15m0 0H4.2M9 15l-6 6"></path>
  <path d="M15 4.2V9m0 0h4.8M15 9l6-6"></path>
  <path d="M9 4.2V9m0 0H4.2M9 9L3 3"></path>
</svg>`;

    // 4. Create zoom toggle button
    const toggle = document.createElement('div');
    toggle.className = 'zoom-toggle';
    toggle.innerHTML = `<span class="icon expand">${expandIcon}</span>`;
    wrapper.appendChild(toggle);
    
    
    //wrap canvas and zoom icon into a div
    if (wrapper) {
        // Create the new wrapper div
        const zoomWrapper = document.createElement('div');
        zoomWrapper.className = 'zoom-canvas-wrapper';
    
        // Move all existing children into the new wrapper
        while (wrapper.firstChild) {
            zoomWrapper.appendChild(wrapper.firstChild);
        }
    
        // Append the new wrapper to the original wrapper
        wrapper.appendChild(zoomWrapper);
    }
    

    let isFullscreen = false;
    let originalWrapperStyle = '';
    let originalCanvasStyle = '';

    function enterFullscreen() {
      if (isFullscreen) return;
      isFullscreen = true;
      
      document.body.classList.add('doogma-canvas-zoom');

      originalWrapperStyle = wrapper.getAttribute('style') || '';
      originalCanvasStyle = canvas.getAttribute('style') || '';

    //   const backdrop = document.createElement('div');
    //   backdrop.className = 'fullscreen-backdrop';
    //   backdrop.setAttribute('data-fullscreen-backdrop', 'true');
    //   document.body.appendChild(backdrop);

      wrapper.classList.add('fullscreen-wrapper');
      document.body.classList.add('visual-fullscreen');

      toggle.innerHTML = `<span class="icon shrink">${shrinkIcon}</span>`;
    }

    function exitFullscreen() {
      if (!isFullscreen) return;
      isFullscreen = false;
      
      document.body.classList.remove('doogma-canvas-zoom');

      const backdrop = document.querySelector('[data-fullscreen-backdrop]');
      if (backdrop) backdrop.remove();

      wrapper.classList.remove('fullscreen-wrapper');
      document.body.classList.remove('visual-fullscreen');

      toggle.innerHTML = `<span class="icon expand">${expandIcon}</span>`;
      wrapper.setAttribute('style', originalWrapperStyle);
      canvas.setAttribute('style', originalCanvasStyle);
    }

    // toggle.addEventListener('click', () => {
    //   if (isFullscreen) {
    //     exitFullscreen();
    //   } else {
    //     enterFullscreen();
    //   }
    // });
    
    // Add both click and touch support
    ['click', 'touchend'].forEach(evt => {
      toggle.addEventListener(evt, (e) => {
        e.preventDefault();
        e.stopPropagation();
        if (isFullscreen) {
          exitFullscreen();
        } else {
          enterFullscreen();
        }
      });
    });

    document.addEventListener('keyup', (e) => {
      if (e.key === 'Escape' && isFullscreen) {
        exitFullscreen();
      }
    });
  });
})();


