                                    <span class="productView-info-value">{{lang 'products.shipping_fixed' amount=product.shipping.price.formatted}}</span>
                                </div>
                            {{/if}}
                        {{/if}}
                    {{/if}}
        
                    
        
                
					
					 {{#if theme_settings.show_custom_fields}}                    

