								{{#startsWith '__' name}}
								{{else}}
								{{#startsWith 's_' name}}
								{{else}}
								    {{#contains name 'bctemplate'}}
								    {{else}}
									<div class="productView-info-item">
										<span class="productView-info-name {{chop name}}">{{name}}:</span>
										<span class="productView-info-value {{chop name}}">{{{ sanitize value}}}</span>
									</div>
									{{/contains}}
									{{/startsWith}}
								{{/startsWith}}
								{{/contains}}
							{{/contains}}
						{{/each}}

					{{/if}}
                </div>
                {{#if product.call_for_price}}
